import { useState } from 'react';
import Recipe_card from './recipe_card';

// import axios from 'axios';
// import send_API_request from '../modules/request_API';


const Choice_menu = () => {
    const [dish_type, setDish_type] = useState('')
    const [add_food, setAdd_food] = useState([]);
    const [exclude_food, setExclude_food] = useState([]);
    const [dish_name, setDish_name] = useState('');
    const [cook_time, setCook_time] = useState(99999);
    const [meal_type, setMeal_type] = useState('');
    const [cuisine, setCuisine] = useState('');
    const [results_number, setResults_number] = useState(1);
    const [response_cards, setReponse_cards] = useState([]);
    const [received_response, setReceived_response] = useState(false)

    const send_input_data = async (req, res) => {
        setReceived_response(false);
        const request_options = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                dish_type,
                dish_name,
                cook_time,
                meal_type,
                cuisine,
                results_number,
                add_food,
                exclude_food

            })
        };
        try {
            const response = await fetch('http://localhost:3030', request_options);
            const data = await response.json();
            setReponse_cards(data);
            setReceived_response(true);
            // console.log('Received data:', data);
            // res.status(200).json({ message: 'Data sent successfully' });
        } catch (error) {
            // Handle any errors that occur during the request
            // res.status(500).json({ error: 'An error occurred while sending the data' });
        }
    }

    return (
        <>
            <div className="container">
                <h1 className="title">Generate your recipe</h1>
                <div id="form">
                    <div className="forms">
                        <div className="form-group">
                            <label >Desired type:</label>
                            <input type="text" placeholder='What kind of food do you want?' id="food_query" onChange={(e) => {
                                setDish_type(e.target.value)
                            }} />
                        </div>
                        <div className="form-group">
                            <label >Add food:</label>
                            <input type="text" placeholder='Include ingredients' onChange={(e) => {
                                setAdd_food(e.target.value)
                            }} />
                        </div>
                        <div className="form-group">
                            <label >Exclude:</label>
                            <input type="text" placeholder='Exclude ingredients' onChange={(e) => {
                                setExclude_food(e.target.value)
                            }} />
                        </div>

                        <div className="form-group">
                            <label >I know the name:</label>
                            <input type="text" placeholder='' id="dish_title" onChange={(e) => {
                                setDish_name(e.target.value)
                            }} />
                        </div>
